import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import "./ProductForm.css"
import { useDispatch } from 'react-redux'
import { createProduct, getProduct } from '../../actions/products';
import { BrowserRouter as history } from 'react-router-dom';
function Registration() {
    const [productData, setproductData] = useState(
        { title:'title', image:'image', price:'price', rating:'rating' }
    );
    
    const dispatch = useDispatch();
    // const history = useNavigate();
    const postProduct =  e => {
        e.preventDefault();
        dispatch(createProduct(productData));
        // history.push("/");

    };
    
    useEffect(() => {
        dispatch(getLogin())
        },[dispatch]);
    return (
        <div className="registration">
            <Link to="/">
                <img 
                    className="registration__Image"
                    src="https://upload.wikimedia.org/wikipedia/commons/thumb/a/a9/Amazon_logo.svg/1024px-Amazon_logo.svg.png"
                    alt=""
                />
            </Link>
            <div className="registration__Container">
                <h1>Register An Account</h1>

                <form>
                    <h5>Title</h5>
                    <input type="text" value={loginData.title} onChange={(e) => setproductData({ ...productData, title: e.target.value})} />

                    <h5>Produce Image</h5>
                    <input type="file" value={productData.image} onChange={(e) => setproductData({ ...productData, image: e.target.value})} />

                    <h5>E-mail</h5>
                    <input type="text" value={productData.price} onChange={(e) => setproductData({ ...productData, price: e.target.value})} />

                    <h5>Password</h5>
                    <input type="password" value={productData.rating} onChange={(e) => setproductData({ ...productData, rating: e.target.value})} />

                    <button type='submit' onClick={register} className='registration__registerButton'></button>
                </form>

                <p>
                    By Signing-in you agree to the Conditions
                    of Use & Sale. Please see out Privacy Notice,
                    Our Cookies Notice and our Interese-Based
                    Ads Notice
                </p>

            </div>
            
        </div>
    )
}

export default Registration
